import Cookies from "js-cookie";
import React, { FunctionComponent, useState } from "react";
import { Link } from 'react-router-dom';

//? assets
import logo from '../../assets/images/logo-hd.webp';
import './style.scss';

//? API
import logIn from "../../api-request/user/log-in";

//? helpers
import isHttpStatusValid from "../../helpers/check-status";
import displayStatusRequest from "../../helpers/display-status-request";

//? components

//? pages

//? models

//? mocks



type Props = {
    ValidLogIn: Function
};
const UserHeader: FunctionComponent<Props> = ({ ValidLogIn }) => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const signIn = (email: string, password: string) => {
        logIn(email, password).then((result) => {
            if (isHttpStatusValid(result.status)) {
                Cookies.set('token', result.response.token, { expires: 1 });
                ValidLogIn()

            } else displayStatusRequest("error " + result.status + " : " + result.response.message, true)
        })
    };

    return (

        <div className='user-login flex-center'>

            <div className="bloc w20 big-dark-container">

                <div className="flex-row flex-start-justify flex-center">
                    <img className='logo m25' src={logo} alt='logo HD'></img>
                    <div className="flex-center  flex-start-align flex-col">
                        <h2 className="m0 orange">CMS HD</h2>
                        <h2 className="m0">Log In</h2>
                    </div>
                </div>


                <form className="flex-col g15 flex-center w100">

                    <div className='form flex-col w100'>
                        <p className='title mt0'>Email</p>
                        <input className='input' type="text" onChange={(e) => setEmail(e.target.value)} />
                        <p className='title'>Password</p>
                        <input className='input' type="password" onChange={(e) => setPassword(e.target.value)} />
                    </div>


                    <div className='button flex-col w100'>
                        <Link to='/recover' className="forget mb25"><span>Foreget password ?</span></Link>
                        <div className='cta mlrauto cta-blue' onClick={() => signIn(email, password)}>
                            Sign In
                        </div>
                    </div>

                </form>
            </div>

        </div>
    )
}
export default UserHeader;
