import React, { FunctionComponent, useState } from "react";
import UserModel from "../../models/user-model";
import "./style.scss";

type Props = {
  currentUser: UserModel;
};
const virtualBoard: { p: number; d: string[] }[][] = [
  [
    { p: 1, d: ["E", "ES", "S"] },
    { p: 1, d: ["E", "S", "W"] },
    { p: 1, d: ["E", "ES", "S", "SW", "W"] },
    { p: 1, d: ["E", "S", "W"] },
    { p: 1, d: ["S", "SW", "W", "WN"] },
  ],
  [
    { p: 1, d: ["N", "E", "S"] },
    { p: 1, d: ["N", "NE", "E", "ES", "S", "SW", "W", "WN"] },
    { p: 1, d: ["N", "E", "S", "W"] },
    { p: 1, d: ["N", "NE", "E", "ES", "S", "SW", "W", "WN"] },
    { p: 1, d: ["N", "S", "W"] },
  ],
  [
    { p: 1, d: ["N", "NE", "E", "ES", "S"] },
    { p: 1, d: ["N", "E", "S", "W"] },
    { p: 0, d: ["N", "NE", "E", "ES", "S", "SW", "W", "WN"] },
    { p: -1, d: ["N", "E", "S", "W"] },
    { p: -1, d: ["N", "S", "SW", "W", "WN"] },
  ],
  [
    { p: -1, d: ["N", "E", "S"] },
    { p: -1, d: ["N", "NE", "E", "ES", "S", "SW", "W", "WN"] },
    { p: -1, d: ["N", "E", "S", "W"] },
    { p: -1, d: ["N", "NE", "E", "ES", "S", "SW", "W", "WN"] },
    { p: -1, d: ["N", "S", "W"] },
  ],
  [
    { p: -1, d: ["N", "NE", "E"] },
    { p: -1, d: ["N", "E", "W"] },
    { p: -1, d: ["N", "NE", "E", "W", "WN"] },
    { p: -1, d: ["N", "E", "W"] },
    { p: -1, d: ["N", "W", "WN"] },
  ],
];
const HomePage1PROJ: FunctionComponent<Props> = ({ currentUser }) => {
  const [P1score, setP1score] = useState<number>(0);
  const [P2score, setP2score] = useState<number>(0);

  return (
    <div className="main p20 flex-col relative flex-end-align g25">
      <div className="flex-col g25 w100">
        <h2 className="m0">Projects : 1PROJ</h2>
        <h1 className="big-dark-container scores flex-center g50">
          <span className="blue">{P1score}</span>
          <span>-</span>
          <span className="red">{P2score}</span>
        </h1>
        <div className="board-container flex-col g25 big-dark-container">
          <div className="board ">
            {virtualBoard.map((e) => e.map((f) => <span className={"case " + f.d.join('-')}>{f.p !== 0 ? <span className={f.p === 1 ? "red-player" : "blue-player"}></span> : null}</span>))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage1PROJ;
